      var nice = 0;

      foreach (string s in input)
      {
        // shorter than 4 doesn't allow a letter-pair repeating
        if (s.Length < 4)
          continue;

        // check for repeating w/ 1 off inbetween
        bool foundRepeating = false;
        for (int i = 0; i < s.Length; i++)
        {
          if (i < s.Length - 2 && s[i] == s[i + 2])
          {
            foundRepeating = true;
            break;
          }
        }
        if (!foundRepeating)
          continue;

        bool foundTwoPairs = false;
        for (int i = 0; i < s.Length; i++)
        {
          if (i > s.Length - 4)
            break;

          var pair = s.Substring(i, 2);
          if (s.IndexOf(pair, i + 2) != -1)
          {
            foundTwoPairs = true;
            break;
          }
        }
        if (!foundTwoPairs)
          continue;

        nice++;
      }

      Console.WriteLine("Santa's list contains " + nice + " nice strings (out of " + input.Count + " strings)...");
